        <div className = "navbar-nav mr-auto">
          <li className="nav-item">
            <Link to={"/restaurants"} className="nav-link">Restaurants</Link>
          </li>
          <li className="nav-item">
            { user ? (
              <a onClick={logout} className="nav-link" style={{cursor:"pointer"}}>Logout {user.name}</a>
