import React, {useState} from "react";
import RestaurantDataService from "../services/restaurant";
import { Link, useLocation, useParams } from "react-router-dom";

function AddReview({user}) {
  const location = useLocation();
  const params = useParams();
  let initialReviewState = "";

  let editing = false;

  if (location.state && location.state.currentReview){
    editing = true;
    initialReviewState = location.state.currentReview.text;
  }

  const [review, setReview] = useState(initialReviewState);
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = event => {
    setReview(event.target.value);
  };

  const saveReview = () => {
    var data = {
      text: review,
      name: user.name,
      user_id: user.id,
      restaurant_id: params.id
    };
  }
  
  return (
    <div>
      {user ? (
        <div className="submit-form">
          {submitted ? (
            <div>
              <h4>You submitted successfully!</h4>
              <Link to={"/restaurants/" + params.id} className="btn">
                Back to Restaurant
              </Link>
            </div>
          ) : (
            <div>
              <div className ="form-group">
                <label htmlFor="description">{editing ? "Edit" : "Create"}</label>
                <input type="text" className="form-control" id="text" required value={review} onChange={handleInputChange} name="text" />
              </div>
            </div>
          )}
        </div>
      ) : (
        <div>

        </div>
      )}
    </div>
  );
}

export default AddReview;
